--
-- Name: accept_invitation(text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.accept_invitation(p_token text) RETURNS uuid
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  v_user_email TEXT;
  v_existing_home_id UUID;
  v_invitation RECORD;
  v_home_id UUID;
BEGIN
  -- Get current user email from JWT
  v_user_email := current_setting('request.jwt.claims', true)::jsonb->>'email';

  IF v_user_email IS NULL THEN
    RAISE EXCEPTION 'not-authenticated';
  END IF;

  -- Check if user already has a home
  SELECT home_id INTO v_existing_home_id
  FROM users
  WHERE email = v_user_email;

  IF v_existing_home_id IS NOT NULL THEN
    RAISE EXCEPTION 'user-already-has-home';
  END IF;

  -- Find and validate the invitation
  SELECT * INTO v_invitation
  FROM home_invitations
  WHERE token = p_token
    AND status = 'pending';

  IF v_invitation.id IS NULL THEN
    RAISE EXCEPTION 'invalid-invitation-token';
  END IF;

  -- Check if invitation has expired
  IF v_invitation.expires_at < now() THEN
    -- Mark as expired
    UPDATE home_invitations SET status = 'expired', responded_at = now() WHERE id = v_invitation.id;
    RAISE EXCEPTION 'invitation-expired';
  END IF;

  -- Check if the invitation is for this user
  IF LOWER(v_invitation.invited_email) != LOWER(v_user_email) THEN
    RAISE EXCEPTION 'invitation-not-for-user';
  END IF;

  v_home_id := v_invitation.home_id;

  -- Update invitation status
  UPDATE home_invitations
  SET status = 'accepted', responded_at = now()
  WHERE id = v_invitation.id;

  -- Update user's home_id and home_joined_at
  UPDATE users SET home_id = v_home_id, home_joined_at = NOW() WHERE email = v_user_email;

  -- Migrate user's orphaned pantry items (home_id IS NULL) to the new home
  UPDATE user_pantry SET home_id = v_home_id WHERE user_email = v_user_email AND home_id IS NULL;

  -- Migrate user's orphaned shopping lists to the new home
  UPDATE shopping_lists SET home_id = v_home_id WHERE user_email = v_user_email AND home_id IS NULL;

  -- Migrate user's orphaned shopping list items to the new home
  UPDATE shopping_list_items SET home_id = v_home_id WHERE user_email = v_user_email AND home_id IS NULL;

  RETURN v_home_id;
END;
$$;


--
-- Name: add_credits(text, integer, text, text, text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.add_credits(p_user_email text, p_amount integer, p_transaction_type text, p_description text DEFAULT NULL::text, p_stripe_payment_intent_id text DEFAULT NULL::text) RETURNS integer
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  v_new_balance INTEGER;
  v_existing_tx UUID;
BEGIN
  -- Idempotency check for Stripe payments
  IF p_stripe_payment_intent_id IS NOT NULL THEN
    SELECT id INTO v_existing_tx
    FROM credit_transactions
    WHERE stripe_payment_intent_id = p_stripe_payment_intent_id;

    IF v_existing_tx IS NOT NULL THEN
      -- Already processed, return current balance
      SELECT balance INTO v_new_balance FROM user_credits WHERE user_email = p_user_email;
      RETURN COALESCE(v_new_balance, 0);
    END IF;
  END IF;

  -- Upsert user_credits row
  INSERT INTO user_credits (user_email, balance, updated_at)
  VALUES (p_user_email, p_amount, now())
  ON CONFLICT (user_email) DO UPDATE
  SET balance = user_credits.balance + p_amount,
      updated_at = now()
  RETURNING balance INTO v_new_balance;

  -- Record transaction
  INSERT INTO credit_transactions (user_email, amount, balance_after, transaction_type, description, stripe_payment_intent_id)
  VALUES (p_user_email, p_amount, v_new_balance, p_transaction_type, p_description, p_stripe_payment_intent_id);

  RETURN v_new_balance;
END;
$$;


--
-- Name: add_recipe_to_shopping_list(uuid, uuid, integer, uuid[]); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.add_recipe_to_shopping_list(p_recipe_id uuid, p_shopping_list_id uuid DEFAULT NULL::uuid, p_servings integer DEFAULT NULL::integer, p_ingredient_ids uuid[] DEFAULT NULL::uuid[]) RETURNS jsonb
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  v_user_email TEXT;
  v_home_id UUID;
  v_list_id UUID;
  v_recipe_name TEXT;
  v_recipe_yield INTEGER;
  v_scale_factor NUMERIC;
  v_added_count INTEGER := 0;
  v_ingredient RECORD;
  v_existing_item_id UUID;
  v_new_item_id UUID;
  v_ingredient_quantity NUMERIC;
  v_scaled_quantity NUMERIC;
  v_canonical_food_id UUID;
BEGIN
  -- Get current user email from JWT
  v_user_email := current_setting('request.jwt.claims', true)::jsonb->>'email';

  IF v_user_email IS NULL THEN
    RAISE EXCEPTION 'not-authenticated';
  END IF;

  -- Get user's home_id
  SELECT home_id INTO v_home_id
  FROM users
  WHERE email = v_user_email;

  IF v_home_id IS NULL THEN
    RAISE EXCEPTION 'user-has-no-home';
  END IF;

  -- Get recipe details
  SELECT name, recipe_yield INTO v_recipe_name, v_recipe_yield
  FROM recipes
  WHERE id = p_recipe_id;

  IF v_recipe_name IS NULL THEN
    RAISE EXCEPTION 'recipe-not-found';
  END IF;

  -- Get or create shopping list
  IF p_shopping_list_id IS NOT NULL THEN
    -- Verify the list exists and belongs to the home
    SELECT id INTO v_list_id
    FROM shopping_lists
    WHERE id = p_shopping_list_id AND home_id = v_home_id;

    IF v_list_id IS NULL THEN
      RAISE EXCEPTION 'shopping-list-not-found';
    END IF;
  ELSE
    v_list_id := get_or_create_default_shopping_list();
  END IF;

  -- Calculate scale factor
  IF p_servings IS NOT NULL AND v_recipe_yield IS NOT NULL AND v_recipe_yield > 0 THEN
    v_scale_factor := p_servings::NUMERIC / v_recipe_yield::NUMERIC;
  ELSE
    v_scale_factor := 1.0;
  END IF;

  -- Process each ingredient
  FOR v_ingredient IN
    SELECT
      i.id,
      i.name,
      i.measurement,
      i.quantity,
      i.food_id,
      i.unit_id
    FROM ingredients i
    WHERE i.recipe_id = p_recipe_id
      AND (p_ingredient_ids IS NULL OR i.id = ANY(p_ingredient_ids))
  LOOP
    -- Parse quantity
    BEGIN
      v_ingredient_quantity := v_ingredient.quantity::NUMERIC;
    EXCEPTION WHEN OTHERS THEN
      v_ingredient_quantity := 1;
    END;

    v_scaled_quantity := v_ingredient_quantity * v_scale_factor;

    -- Resolve ingredient food_id to canonical for merge check
    IF v_ingredient.food_id IS NOT NULL THEN
      v_canonical_food_id := resolve_canonical(v_ingredient.food_id);

      -- Check for existing unchecked item with same canonical food_id AND unit_id
      SELECT sli.id INTO v_existing_item_id
      FROM shopping_list_items sli
      LEFT JOIN foods f ON f.id = sli.food_id
      WHERE sli.shopping_list_id = v_list_id
        AND COALESCE(f.canonical_food_id, sli.food_id) = v_canonical_food_id
        AND sli.unit_id IS NOT DISTINCT FROM v_ingredient.unit_id
        AND sli.is_checked = false;
    ELSE
      v_existing_item_id := NULL;
    END IF;

    IF v_existing_item_id IS NOT NULL THEN
      -- Add quantity to existing item
      UPDATE shopping_list_items
      SET quantity = quantity + v_scaled_quantity
      WHERE id = v_existing_item_id;

      v_new_item_id := v_existing_item_id;
    ELSE
      -- Insert new item
      INSERT INTO shopping_list_items (
        shopping_list_id,
        home_id,
        food_id,
        unit_id,
        display_name,
        display_unit,
        quantity,
        user_email,
        sort_order
      )
      VALUES (
        v_list_id,
        v_home_id,
        v_ingredient.food_id,
        v_ingredient.unit_id,
        v_ingredient.name,
        COALESCE(v_ingredient.measurement, ''),
        v_scaled_quantity,
        v_user_email,
        (SELECT COALESCE(MAX(sort_order), 0) + 1 FROM shopping_list_items WHERE shopping_list_id = v_list_id)
      )
      RETURNING id INTO v_new_item_id;
    END IF;

    -- Insert source tracking record
    INSERT INTO shopping_list_item_sources (
      shopping_list_item_id,
      recipe_id,
      recipe_name,
      quantity_added,
      servings_used,
      user_email
    )
    VALUES (
      v_new_item_id,
      p_recipe_id,
      v_recipe_name,
      v_scaled_quantity,
      p_servings,
      v_user_email
    );

    v_added_count := v_added_count + 1;
  END LOOP;

  -- Update shopping list modified timestamp
  UPDATE shopping_lists SET date_modified = now() WHERE id = v_list_id;

  RETURN jsonb_build_object(
    'added_count', v_added_count,
    'list_id', v_list_id
  );
END;
$$;


--
-- Name: add_to_pantry(uuid, numeric, text, date); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.add_to_pantry(p_food_id uuid, p_quantity numeric DEFAULT NULL::numeric, p_unit text DEFAULT NULL::text, p_expires_at date DEFAULT NULL::date) RETURNS uuid
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  v_user_email TEXT;
  v_home_id UUID;
  v_pantry_id UUID;
BEGIN
  -- Get current user email from JWT
  v_user_email := current_setting('request.jwt.claims', true)::jsonb->>'email';

  IF v_user_email IS NULL THEN
    RAISE EXCEPTION 'not-authenticated';
  END IF;

  -- Get user's home_id
  SELECT home_id INTO v_home_id
  FROM users
  WHERE email = v_user_email;

  IF v_home_id IS NULL THEN
    RAISE EXCEPTION 'user-has-no-home';
  END IF;

  -- Validate food_id exists
  IF NOT EXISTS (SELECT 1 FROM foods WHERE id = p_food_id) THEN
    RAISE EXCEPTION 'food-not-found';
  END IF;

  -- Upsert into pantry (now keyed by home_id + food_id)
  INSERT INTO user_pantry (user_email, home_id, food_id, quantity, unit, expires_at)
  VALUES (v_user_email, v_home_id, p_food_id, p_quantity, p_unit, p_expires_at)
  ON CONFLICT (home_id, food_id) DO UPDATE SET
    quantity = EXCLUDED.quantity,
    unit = EXCLUDED.unit,
    expires_at = EXCLUDED.expires_at,
    added_at = NOW(),
    user_email = EXCLUDED.user_email
  RETURNING id INTO v_pantry_id;

  RETURN v_pantry_id;
END;
$$;


