-- Name: admin_count_foods(text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.admin_count_foods(p_search text DEFAULT NULL::text) RETURNS bigint
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  v_count BIGINT;
BEGIN
  -- Check admin permission
  IF NOT is_admin() THEN
    RAISE EXCEPTION 'Access denied: admin privileges required';
  END IF;

  -- Return total count
  SELECT COUNT(*) INTO v_count
  FROM foods f
  WHERE
    -- Handle NULL/empty search - return all
    p_search IS NULL
    OR trim(p_search) = ''
    -- Full-text search
    OR f.tsv @@ plainto_tsquery('swedish', p_search)
    -- Partial ILIKE match
    OR f.name ILIKE '%' || p_search || '%';

  RETURN v_count;
END;
$$;


