--
-- Name: admin_delete_user(uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.admin_delete_user(p_user_id uuid) RETURNS void
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  v_current_email TEXT;
  v_target_email TEXT;
BEGIN
  -- Check admin permission
  IF NOT is_admin() THEN
    RAISE EXCEPTION 'Access denied: admin privileges required';
  END IF;

  -- Get current admin's email from JWT
  v_current_email := current_setting('request.jwt.claims', true)::jsonb->>'email';

  -- Get target user's email
  SELECT email INTO v_target_email
  FROM users
  WHERE id = p_user_id;

  -- Check if user exists
  IF v_target_email IS NULL THEN
    RAISE EXCEPTION 'user-not-found';
  END IF;

  -- Prevent self-deletion
  IF v_current_email = v_target_email THEN
    RAISE EXCEPTION 'cannot-delete-self: you cannot delete your own account via admin panel';
  END IF;

  -- Delete the user (will cascade to user_passwords via FK)
  DELETE FROM users
  WHERE id = p_user_id;
END;
$$;


--
-- Name: admin_list_foods(text, public.food_status, integer, integer); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.admin_list_foods(p_search text DEFAULT NULL::text, p_status public.food_status DEFAULT NULL::public.food_status, p_limit integer DEFAULT 50, p_offset integer DEFAULT 0) RETURNS TABLE(id uuid, name text, status public.food_status, created_by text, reviewed_by text, reviewed_at timestamp with time zone, date_published timestamp with time zone, date_modified timestamp with time zone, ingredient_count bigint, canonical_food_id uuid, canonical_food_name text)
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
BEGIN
  IF NOT is_admin() THEN
    RAISE EXCEPTION 'Access denied: admin privileges required';
  END IF;

  RETURN QUERY
  SELECT
    f.id,
    f.name,
    f.status,
    f.created_by,
    f.reviewed_by,
    f.reviewed_at,
    f.date_published,
    f.date_modified,
    COUNT(i.id) AS ingredient_count,
    f.canonical_food_id,
    cf.name AS canonical_food_name
  FROM foods f
  LEFT JOIN ingredients i ON i.food_id = f.id
  LEFT JOIN foods cf ON cf.id = f.canonical_food_id
  WHERE
    (p_status IS NULL OR f.status = p_status)
    AND (
      p_search IS NULL
      OR trim(p_search) = ''
      OR f.tsv @@ plainto_tsquery('swedish', p_search)
      OR f.name ILIKE '%' || escape_like_pattern(p_search) || '%'
    )
  GROUP BY f.id, f.name, f.status, f.created_by, f.reviewed_by, f.reviewed_at,
           f.date_published, f.date_modified, f.canonical_food_id, cf.name
  ORDER BY
    CASE
      WHEN p_search IS NULL OR trim(p_search) = '' THEN 0
      WHEN lower(f.name) = lower(trim(p_search)) THEN 1.0
      WHEN lower(f.name) LIKE lower(escape_like_pattern(trim(p_search))) || '%' THEN 0.9
      ELSE ts_rank(f.tsv, plainto_tsquery('swedish', p_search))
    END DESC,
    f.name
  LIMIT p_limit
  OFFSET p_offset;
END;
$$;


--
-- Name: admin_list_units(text, integer, integer); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.admin_list_units(p_search text DEFAULT NULL::text, p_limit integer DEFAULT 50, p_offset integer DEFAULT 0) RETURNS TABLE(id uuid, name text, plural text, abbreviation text, date_published timestamp with time zone, date_modified timestamp with time zone, ingredient_count bigint)
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
BEGIN
  -- Check admin permission
  IF NOT is_admin() THEN
    RAISE EXCEPTION 'Access denied: admin privileges required';
  END IF;

  -- Return paginated results ordered by relevance
  RETURN QUERY
  SELECT
    u.id,
    u.name,
    u.plural,
    u.abbreviation,
    u.date_published,
    u.date_modified,
    COUNT(i.id) AS ingredient_count
  FROM units u
  LEFT JOIN ingredients i ON i.unit_id = u.id
  WHERE
    -- Handle NULL/empty search - return all
    p_search IS NULL
    OR trim(p_search) = ''
    -- Full-text search
    OR u.tsv @@ plainto_tsquery('swedish', p_search)
    -- Partial ILIKE match on any field
    OR u.name ILIKE '%' || p_search || '%'
    OR u.plural ILIKE '%' || p_search || '%'
    OR u.abbreviation ILIKE '%' || p_search || '%'
  GROUP BY u.id, u.name, u.plural, u.abbreviation, u.date_published, u.date_modified
  ORDER BY
    -- Relevance ranking using ts_rank
    CASE
      WHEN p_search IS NULL OR trim(p_search) = '' THEN 0
      -- Exact match on name or abbreviation: highest priority
      WHEN lower(u.name) = lower(trim(p_search)) OR lower(u.abbreviation) = lower(trim(p_search)) THEN 1.0
      -- Starts with search term: high priority
      WHEN lower(u.name) LIKE lower(trim(p_search)) || '%' OR lower(u.abbreviation) LIKE lower(trim(p_search)) || '%' THEN 0.9
      -- Full-text search rank
      ELSE ts_rank(u.tsv, plainto_tsquery('swedish', p_search))
    END DESC,
    u.name
  LIMIT p_limit
  OFFSET p_offset;
END;
$$;


--
-- Name: admin_list_users(text, text, integer, integer); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.admin_list_users(p_search text DEFAULT NULL::text, p_role text DEFAULT NULL::text, p_limit integer DEFAULT 50, p_offset integer DEFAULT 0) RETURNS TABLE(id uuid, name text, email text, role text, provider text, recipe_count bigint)
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
BEGIN
  -- Check admin permission
  IF NOT is_admin() THEN
    RAISE EXCEPTION 'Access denied: admin privileges required';
  END IF;

  -- Return paginated results with search and role filtering
  RETURN QUERY
  SELECT
    u.id,
    u.name,
    u.email,
    u.role,
    u.provider,
    COUNT(r.id) AS recipe_count
  FROM users u
  LEFT JOIN recipes r ON r.owner = u.email
  WHERE
    -- Role filter
    (p_role IS NULL OR u.role = p_role)
    AND (
      -- Handle NULL/empty search - return all
      p_search IS NULL
      OR trim(p_search) = ''
      -- Full-text search on name and email
      OR to_tsvector('swedish', u.name || ' ' || u.email) @@ plainto_tsquery('swedish', p_search)
      -- Partial ILIKE match fallback
      OR u.name ILIKE '%' || p_search || '%'
      OR u.email ILIKE '%' || p_search || '%'
    )
  GROUP BY u.id, u.name, u.email, u.role, u.provider
  ORDER BY
    -- Relevance ranking
    CASE
      WHEN p_search IS NULL OR trim(p_search) = '' THEN 0
      -- Exact match on name or email: highest priority
      WHEN lower(u.name) = lower(trim(p_search)) OR lower(u.email) = lower(trim(p_search)) THEN 1.0
      -- Starts with search term: high priority
      WHEN lower(u.name) LIKE lower(trim(p_search)) || '%' OR lower(u.email) LIKE lower(trim(p_search)) || '%' THEN 0.9
      -- Full-text search rank
      ELSE ts_rank(to_tsvector('swedish', u.name || ' ' || u.email), plainto_tsquery('swedish', p_search))
    END DESC,
    u.name
  LIMIT p_limit
  OFFSET p_offset;
END;
