-- Name: clear_checked_items(uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.clear_checked_items(p_shopping_list_id uuid DEFAULT NULL::uuid) RETURNS jsonb
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  v_user_email TEXT;
  v_home_id UUID;
  v_list_id UUID;
  v_deleted_count INTEGER;
BEGIN
  -- Get current user email from JWT
  v_user_email := current_setting('request.jwt.claims', true)::jsonb->>'email';

  IF v_user_email IS NULL THEN
    RAISE EXCEPTION 'not-authenticated';
  END IF;

  -- Get user's home_id
  SELECT home_id INTO v_home_id
  FROM users
  WHERE email = v_user_email;

  IF v_home_id IS NULL THEN
    RAISE EXCEPTION 'user-has-no-home';
  END IF;

  -- Get or default to default shopping list
  IF p_shopping_list_id IS NOT NULL THEN
    -- Verify the list exists and belongs to the home
    SELECT id INTO v_list_id
    FROM shopping_lists
    WHERE id = p_shopping_list_id AND home_id = v_home_id;

    IF v_list_id IS NULL THEN
      RAISE EXCEPTION 'shopping-list-not-found';
    END IF;
  ELSE
    v_list_id := get_or_create_default_shopping_list();
  END IF;

  -- Delete checked items (sources will cascade delete)
  DELETE FROM shopping_list_items
  WHERE shopping_list_id = v_list_id AND is_checked = true;

  GET DIAGNOSTICS v_deleted_count = ROW_COUNT;

  -- Update shopping list modified timestamp
  UPDATE shopping_lists SET date_modified = now() WHERE id = v_list_id;

  RETURN jsonb_build_object('deleted_count', v_deleted_count);
END;
$$;


--
-- Name: complete_password_reset(text, text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.complete_password_reset(p_token text, p_new_password text) RETURNS jsonb
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public', 'extensions'
    AS $$
DECLARE
    v_token_hash TEXT;
    v_user_email TEXT;
    v_token_id UUID;
BEGIN
    -- Validate password requirements
    IF p_new_password IS NULL OR
       LENGTH(p_new_password) < 8 OR
       NOT (p_new_password ~* '.*[A-Z].*') OR
       NOT (p_new_password ~* '.*[a-z].*') OR
       NOT (p_new_password ~ '\d') THEN
        RAISE EXCEPTION 'password-not-meet-requirements';
    END IF;

    -- Hash the provided token
    v_token_hash := encode(sha256(p_token::bytea), 'hex');

    -- Find the token, verify not expired and not used
    -- Use FOR UPDATE SKIP LOCKED to prevent race conditions when multiple
    -- requests try to use the same token simultaneously
    SELECT id, user_email
    INTO v_token_id, v_user_email
    FROM password_reset_tokens
    WHERE token_hash = v_token_hash
      AND expires_at > now()
      AND used_at IS NULL
    FOR UPDATE SKIP LOCKED;

    IF v_token_id IS NULL THEN
        RAISE EXCEPTION 'invalid-or-expired-token';
    END IF;

    -- Update the user's password (trigger will hash it)
    UPDATE user_passwords
    SET password = p_new_password
    WHERE email = v_user_email;

    -- If no password record exists (e.g., OAuth user), create one
    IF NOT FOUND THEN
        INSERT INTO user_passwords (email, password, owner)
        VALUES (v_user_email, p_new_password, v_user_email);
    END IF;

    -- Mark token as used
    UPDATE password_reset_tokens
    SET used_at = now()
    WHERE id = v_token_id;

    RETURN jsonb_build_object('success', true);
END;
$$;


--
-- Name: copy_recipe(uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.copy_recipe(p_source_recipe_id uuid) RETURNS uuid
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  v_user_email TEXT;
  v_new_recipe_id UUID;
  v_original_author_name TEXT;
  v_source_recipe RECORD;
BEGIN
  -- Get current user from JWT
  v_user_email := current_setting('request.jwt.claims', true)::jsonb->>'email';

  IF v_user_email IS NULL THEN
    RAISE EXCEPTION 'not-authenticated';
  END IF;

  -- Verify user can see the source recipe (RLS check via SELECT)
  -- Note: SECURITY DEFINER bypasses RLS for INSERT but we manually check SELECT visibility
  -- by querying the recipe. If the user cannot see it due to RLS, this returns NULL.
  SELECT r.id, r.name, r.author, r.url, r.recipe_yield, r.recipe_yield_name,
         r.prep_time, r.cook_time, r.cuisine, r.description, r.image, r.thumbnail,
         r.owner, u.name AS owner_display_name
  INTO v_source_recipe
  FROM recipes r
  LEFT JOIN users u ON u.email = r.owner
  WHERE r.id = p_source_recipe_id;

  IF v_source_recipe.id IS NULL THEN
    RAISE EXCEPTION 'recipe-not-found-or-not-visible';
  END IF;

  -- Get original author display name for attribution (fallback to email if no name)
  v_original_author_name := COALESCE(v_source_recipe.owner_display_name, v_source_recipe.owner);

  -- ==========================================================================
  -- Copy recipe (owner becomes current user, visibility defaults to private)
  -- ==========================================================================
  INSERT INTO recipes (
    name,
    author,
    url,
    recipe_yield,
    recipe_yield_name,
    prep_time,
    cook_time,
    cuisine,
    description,
    image,
    thumbnail,
    owner,
    visibility,
    copied_from_recipe_id,
    copied_from_user_id,
    copied_from_author_name
  )
  VALUES (
    v_source_recipe.name,
    v_source_recipe.author,
    v_source_recipe.url,
    v_source_recipe.recipe_yield,
    v_source_recipe.recipe_yield_name,
    v_source_recipe.prep_time,
    v_source_recipe.cook_time,
    v_source_recipe.cuisine,
    v_source_recipe.description,
    v_source_recipe.image,
    v_source_recipe.thumbnail,
    v_user_email,
    'private'::recipe_visibility,
    p_source_recipe_id,
    v_source_recipe.owner,
    v_original_author_name
  )
  RETURNING id INTO v_new_recipe_id;

  -- ==========================================================================
  -- Copy ingredient_groups with ID remapping
  -- Use sort_order as correlation key (unique within recipe)
  -- ==========================================================================
  WITH source_groups AS (
    SELECT id AS old_id, name, sort_order
    FROM ingredient_groups
    WHERE recipe_id = p_source_recipe_id
  ),
  inserted_groups AS (
    INSERT INTO ingredient_groups (recipe_id, name, sort_order, owner)
    SELECT v_new_recipe_id, sg.name, sg.sort_order, v_user_email
    FROM source_groups sg
    RETURNING id AS new_id, sort_order
  ),
  group_mapping AS (
    SELECT sg.old_id, ig.new_id
    FROM source_groups sg
    JOIN inserted_groups ig ON ig.sort_order = sg.sort_order
  )
  -- Copy ingredients with remapped group_id
  INSERT INTO ingredients (
    recipe_id,
    name,
    measurement,
    quantity,
    food_id,
    unit_id,
    form,
    group_id,
    sort_order,
    owner
  )
  SELECT
    v_new_recipe_id,
    i.name,
    i.measurement,
    i.quantity,
    i.food_id,
    i.unit_id,
    i.form,
    gm.new_id,  -- Remapped group_id (NULL if original was NULL)
    i.sort_order,
    v_user_email
  FROM ingredients i
  LEFT JOIN group_mapping gm ON gm.old_id = i.group_id
  WHERE i.recipe_id = p_source_recipe_id;

  -- ==========================================================================
  -- Copy instruction_groups with ID remapping
  -- Use sort_order as correlation key (unique within recipe)
  -- ==========================================================================
  WITH source_inst_groups AS (
    SELECT id AS old_id, name, sort_order
    FROM instruction_groups
    WHERE recipe_id = p_source_recipe_id
  ),
  inserted_inst_groups AS (
    INSERT INTO instruction_groups (recipe_id, name, sort_order, owner)
    SELECT v_new_recipe_id, sig.name, sig.sort_order, v_user_email
    FROM source_inst_groups sig
    RETURNING id AS new_id, sort_order
  ),
  inst_group_mapping AS (
    SELECT sig.old_id, iig.new_id
    FROM source_inst_groups sig
    JOIN inserted_inst_groups iig ON iig.sort_order = sig.sort_order
  )
  -- Copy instructions with remapped group_id
  INSERT INTO instructions (
    recipe_id,
    step,
    group_id,
    sort_order,
    owner
  )
  SELECT
    v_new_recipe_id,
    inst.step,
    igm.new_id,  -- Remapped group_id (NULL if original was NULL)
    inst.sort_order,
    v_user_email
  FROM instructions inst
  LEFT JOIN inst_group_mapping igm ON igm.old_id = inst.group_id
  WHERE inst.recipe_id = p_source_recipe_id;

  -- ==========================================================================
  -- Copy categories (junction table - no ID remapping needed)
  -- ==========================================================================
  INSERT INTO recipe_categories (recipe, category, owner)
  SELECT v_new_recipe_id, category, v_user_email
  FROM recipe_categories
  WHERE recipe = p_source_recipe_id;

  RETURN v_new_recipe_id;
END;
$$;


--
-- Name: create_home(text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.create_home(p_name text) RETURNS uuid
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  v_user_email TEXT;
  v_existing_home_id UUID;
  v_home_id UUID;
BEGIN
  -- Get current user email from JWT
  v_user_email := current_setting('request.jwt.claims', true)::jsonb->>'email';

  IF v_user_email IS NULL THEN
    RAISE EXCEPTION 'not-authenticated';
  END IF;

  -- Check if user already has a home
  SELECT home_id INTO v_existing_home_id
  FROM users
  WHERE email = v_user_email;

  IF v_existing_home_id IS NOT NULL THEN
    RAISE EXCEPTION 'user-already-has-home';
  END IF;

  -- Validate name
  IF p_name IS NULL OR LENGTH(TRIM(p_name)) < 1 THEN
    RAISE EXCEPTION 'invalid-home-name';
  END IF;

  -- Create the home
  INSERT INTO homes (name, created_by_email)
  VALUES (TRIM(p_name), v_user_email)
  RETURNING id INTO v_home_id;

  -- Update user's home_id and home_joined_at
  UPDATE users SET home_id = v_home_id, home_joined_at = NOW() WHERE email = v_user_email;

  -- Migrate user's orphaned pantry items (home_id IS NULL) to the new home
  UPDATE user_pantry SET home_id = v_home_id WHERE user_email = v_user_email AND home_id IS NULL;

  -- Migrate user's orphaned shopping lists to the new home
  UPDATE shopping_lists SET home_id = v_home_id WHERE user_email = v_user_email AND home_id IS NULL;

  -- Migrate user's orphaned shopping list items to the new home
  UPDATE shopping_list_items SET home_id = v_home_id WHERE user_email = v_user_email AND home_id IS NULL;

  RETURN v_home_id;
END;
$$;


