    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public', 'extensions'
    AS $$
BEGIN
  -- Only encrypt if password is not null and has changed
  IF NEW.password IS NOT NULL AND (tg_op = 'INSERT' OR NEW.password <> OLD.password) THEN
