CREATE FUNCTION public.find_similar_foods(p_name text, p_limit integer DEFAULT 5) RETURNS TABLE(id uuid, name text, status public.food_status, similarity_score real)
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public', 'extensions'
    AS $$
BEGIN
  -- Check admin permission
  IF NOT is_admin() THEN
    RAISE EXCEPTION 'Access denied: admin privileges required';
  END IF;

  -- Return similar foods using trigram similarity
  RETURN QUERY
  SELECT
    f.id,
    f.name,
