--

CREATE FUNCTION public.pre_request() RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_api_key TEXT;
  v_user_email TEXT;
BEGIN
  v_api_key := current_setting('request.headers', true)::json->>'x-api-key';

  IF v_api_key IS NOT NULL AND v_api_key != '' THEN
    v_user_email := validate_api_key(v_api_key);

    IF v_user_email IS NOT NULL THEN
      SET LOCAL role TO 'authenticated';
      PERFORM set_config(
        'request.jwt.claims',
        json_build_object('email', v_user_email, 'role', 'authenticated')::text,
        true
      );
    ELSE
      RAISE EXCEPTION 'Invalid or expired API key'
        USING ERRCODE = '28000';
    END IF;
  END IF;
END;
$$;


--
-- Name: prevent_role_escalation(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.prevent_role_escalation() RETURNS trigger
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  current_user_email TEXT;
  current_user_role TEXT;
BEGIN
  -- Only check if role column is being changed
  IF NEW.role IS DISTINCT FROM OLD.role THEN
    BEGIN
      -- Get current user email from JWT (with error handling)
      current_user_email := current_setting('request.jwt.claims', true)::jsonb->>'email';
    EXCEPTION
      WHEN OTHERS THEN
        current_user_email := NULL;
    END;

    -- If no JWT email, deny the change (not authenticated)
    IF current_user_email IS NULL THEN
      RAISE EXCEPTION 'permission-denied'
        USING HINT = 'Authentication required',
              ERRCODE = '42501';
    END IF;

    -- Get current user's role from database
    SELECT role INTO current_user_role
    FROM users
    WHERE email = current_user_email;

    -- SECURITY FIX: Generic error message - do not disclose role
    IF current_user_role IS NULL OR current_user_role != 'admin' THEN
      RAISE EXCEPTION 'permission-denied'
        USING HINT = 'Insufficient privileges',
              ERRCODE = '42501';
    END IF;
  END IF;

  RETURN NEW;
END;
$$;


--
-- Name: queue_email(text, text, jsonb, jsonb, text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.queue_email(p_template_name text, p_recipient_email text, p_variables jsonb DEFAULT '{}'::jsonb, p_metadata jsonb DEFAULT '{}'::jsonb, p_language text DEFAULT 'sv'::text) RETURNS uuid
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
    v_template_id UUID;
    v_email_id UUID;
    v_user_id UUID;
BEGIN
    -- Get the template
    v_template_id := get_email_template(p_template_name, p_language);

    -- Try to find the user by email
    SELECT id INTO v_user_id
    FROM users
    WHERE email = p_recipient_email;

    -- Queue the email with explicit owner (use recipient email or system for unauthenticated contexts)
    INSERT INTO email_messages (
        template_id,
        recipient_email,
        recipient_user_id,
        variables,
        metadata,
        owner
    ) VALUES (
        v_template_id,
        p_recipient_email,
        v_user_id,
        p_variables,
        p_metadata || jsonb_build_object('language', p_language),
        COALESCE(
            NULLIF(current_setting('request.jwt.claims', true)::jsonb->>'email', ''),
            'system@matrummet.local'
        )
    )
    RETURNING id INTO v_email_id;

    RETURN v_email_id;
END;
$$;


