--
-- Name: request_password_reset(text, text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.request_password_reset(p_email text, p_app_url text DEFAULT NULL::text) RETURNS jsonb
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public', 'extensions'
    AS $$
DECLARE
    v_user_exists BOOLEAN;
    v_user_name TEXT;
    v_last_request_at TIMESTAMPTZ;
    v_token TEXT;
    v_token_hash TEXT;
    v_reset_link TEXT;
    v_base_url TEXT;
    v_rate_limit_interval INTERVAL := INTERVAL '5 minutes';
BEGIN
    -- Check if user exists (including OAuth-only users without passwords)
    -- This allows OAuth users to add password login to their account
    SELECT EXISTS(
        SELECT 1 FROM users
        WHERE email = p_email
    ) INTO v_user_exists;

    -- If user exists, check rate limit and potentially generate token
    IF v_user_exists THEN
        -- Check when the last token was created for this email
        SELECT date_published INTO v_last_request_at
        FROM password_reset_tokens
        WHERE user_email = p_email
        ORDER BY date_published DESC
        LIMIT 1;

        -- Rate limit check: if last request was within 5 minutes, silently return success
        -- This prevents email enumeration while still rate limiting actual requests
        IF v_last_request_at IS NOT NULL AND v_last_request_at > now() - v_rate_limit_interval THEN
            -- Return success without creating token or sending email
            RETURN jsonb_build_object('success', true);
        END IF;

        -- Get user name for email
        SELECT name INTO v_user_name FROM users WHERE email = p_email;

        -- Generate a random token
        v_token := gen_random_uuid()::TEXT;

        -- Store the SHA256 hash of the token
        v_token_hash := encode(sha256(v_token::bytea), 'hex');

        -- Invalidate any existing unused tokens for this user
        UPDATE password_reset_tokens
        SET used_at = now()
        WHERE user_email = p_email
          AND used_at IS NULL;

        -- Insert the new token (expires in 24 hours)
        INSERT INTO password_reset_tokens (user_email, token_hash, expires_at)
        VALUES (p_email, v_token_hash, now() + INTERVAL '24 hours');

        -- Build reset link using provided URL or fallback
        v_base_url := COALESCE(
            p_app_url,
            current_setting('app.base_url', true),
            'https://matrummet.app'
        );
        v_reset_link := v_base_url || '/reset-password/' || v_token;

        -- Queue the password reset email
        PERFORM queue_email(
            'password_reset',
            p_email,
            jsonb_build_object(
                'user_name', COALESCE(v_user_name, 'Anvandare'),
                'reset_link', v_reset_link
            )
        );
    END IF;

    -- Always return success to avoid revealing if email exists
    RETURN jsonb_build_object('success', true);
END;
$$;


--
-- Name: reset_password(text, text, text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.reset_password(p_email text, p_old_password text, p_new_password text) RETURNS void
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public', 'extensions'
    AS $$
DECLARE
    stored_password text;
    jwt_email text;
BEGIN
    -- SECURITY FIX: Validate that the caller is resetting their own password
    jwt_email := current_setting('request.jwt.claims', true)::jsonb->>'email';

    IF jwt_email IS NULL OR jwt_email <> p_email THEN
        RAISE EXCEPTION 'unauthorized-password-reset';
    END IF;

    SELECT password INTO stored_password FROM user_passwords WHERE user_passwords.email = p_email;

    IF stored_password IS NULL THEN
        RAISE EXCEPTION 'invalid-credentials';
    END IF;

    IF stored_password <> crypt(p_old_password, stored_password) THEN
        RAISE EXCEPTION 'invalid-credentials';
    END IF;

    -- SECURITY FIX: Added max length check (72 bytes = bcrypt limit) to prevent DoS
    IF LENGTH(p_new_password) < 8 OR
       LENGTH(p_new_password) > 72 OR
       NOT (p_new_password ~* '.*[A-Z].*') OR
       NOT (p_new_password ~* '.*[a-z].*') OR
       NOT (p_new_password ~ '\d') THEN
        RAISE EXCEPTION 'password-not-meet-requirements';
    END IF;

    UPDATE user_passwords
    SET password = p_new_password
    WHERE user_passwords.email = p_email;
END;
$$;


