--
-- Name: search_recipes(text, boolean, text, integer, integer); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.search_recipes(p_query text, p_owner_only boolean DEFAULT false, p_category text DEFAULT NULL::text, p_limit integer DEFAULT 50, p_offset integer DEFAULT 0) RETURNS SETOF public.user_recipes
    LANGUAGE sql STABLE
    SET search_path TO 'public', 'extensions'
    AS $$
  SELECT ur.* FROM user_recipes ur JOIN recipes r ON r.id = ur.id
  WHERE COALESCE(trim(p_query), '') != '' AND r.search_text ILIKE '%' || escape_like_pattern(p_query) || '%'
    AND (NOT p_owner_only OR ur.is_owner = TRUE)
    AND (p_category IS NULL OR p_category = ANY(ur.categories))
  ORDER BY CASE WHEN ur.name ILIKE escape_like_pattern(p_query) THEN 0 WHEN ur.name ILIKE escape_like_pattern(p_query) || '%' THEN 1 WHEN ur.name ILIKE '%' || escape_like_pattern(p_query) || '%' THEN 2 ELSE 3 END, word_similarity(p_query, ur.name) DESC, ur.date_published DESC
  LIMIT p_limit OFFSET p_offset;
$$;


--
-- Name: search_units(text, integer); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.search_units(p_query text, p_limit integer DEFAULT 10) RETURNS TABLE(id uuid, name text, plural text, abbreviation text, rank real)
    LANGUAGE sql STABLE
    AS $$
  SELECT
    u.id,
    u.name,
    u.plural,
    u.abbreviation,
    CASE
      WHEN lower(u.name) = lower(trim(p_query)) THEN 1.0
      WHEN lower(u.abbreviation) = lower(trim(p_query)) THEN 0.95
      WHEN lower(u.name) LIKE lower(trim(p_query)) || '%' THEN 0.9
      WHEN lower(u.abbreviation) LIKE lower(trim(p_query)) || '%' THEN 0.85
      ELSE ts_rank(u.tsv, plainto_tsquery('swedish'::regconfig, p_query))
    END AS rank
  FROM units u
  WHERE u.tsv @@ plainto_tsquery('swedish'::regconfig, p_query)
     OR u.name ILIKE trim(p_query) || '%'
     OR u.abbreviation ILIKE trim(p_query) || '%'
  ORDER BY
    CASE
      WHEN lower(u.name) = lower(trim(p_query)) THEN 0
      WHEN lower(u.abbreviation) = lower(trim(p_query)) THEN 1
      WHEN lower(u.name) LIKE lower(trim(p_query)) || '%' THEN 2
      WHEN lower(u.abbreviation) LIKE lower(trim(p_query)) || '%' THEN 3
      ELSE 4
    END,
    ts_rank(u.tsv, plainto_tsquery('swedish'::regconfig, p_query)) DESC,
    u.name
  LIMIT p_limit;
$$;


