--

CREATE FUNCTION public.set_default_shopping_list(p_list_id uuid) RETURNS void
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  v_user_email TEXT;
  v_home_id UUID;
  v_list_exists BOOLEAN;
BEGIN
  -- Get current user email from JWT
  v_user_email := current_setting('request.jwt.claims', true)::jsonb->>'email';

  IF v_user_email IS NULL THEN
    RAISE EXCEPTION 'not-authenticated';
  END IF;

  -- Get user's home_id
  SELECT home_id INTO v_home_id
  FROM users
  WHERE email = v_user_email;

  IF v_home_id IS NULL THEN
    RAISE EXCEPTION 'user-has-no-home';
  END IF;

  -- Check if list exists and belongs to home
  SELECT EXISTS (
    SELECT 1 FROM shopping_lists
    WHERE id = p_list_id AND home_id = v_home_id
  ) INTO v_list_exists;

  IF NOT v_list_exists THEN
    RAISE EXCEPTION 'shopping-list-not-found';
  END IF;

  -- Remove default from all other lists
  UPDATE shopping_lists
  SET is_default = false
  WHERE home_id = v_home_id AND is_default = true;

  -- Set the new default
  UPDATE shopping_lists
  SET is_default = true
  WHERE id = p_list_id AND home_id = v_home_id;
END;
$$;


--
-- Name: set_food_canonical(uuid, uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.set_food_canonical(p_food_id uuid, p_canonical_food_id uuid DEFAULT NULL::uuid) RETURNS void
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
BEGIN
  IF NOT is_admin() THEN
    RAISE EXCEPTION 'Access denied: admin privileges required';
  END IF;

  -- Validate food exists
  IF NOT EXISTS (SELECT 1 FROM foods WHERE id = p_food_id) THEN
    RAISE EXCEPTION 'Food with id % not found', p_food_id;
  END IF;

  -- If setting (not clearing), validate target
  IF p_canonical_food_id IS NOT NULL THEN
    IF NOT EXISTS (SELECT 1 FROM foods WHERE id = p_canonical_food_id) THEN
      RAISE EXCEPTION 'Canonical food with id % not found', p_canonical_food_id;
    END IF;

    IF p_food_id = p_canonical_food_id THEN
      RAISE EXCEPTION 'A food cannot be an alias of itself';
    END IF;
  END IF;

  -- The chain prevention trigger will handle validation
  UPDATE foods
  SET canonical_food_id = p_canonical_food_id
  WHERE id = p_food_id;

  IF NOT FOUND THEN
    RAISE EXCEPTION 'Food with id % not found', p_food_id;
  END IF;
END;
$$;


