--

CREATE FUNCTION public.shares_household_with(p_owner_email text) RETURNS boolean
    LANGUAGE plpgsql STABLE SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  v_current_user_email TEXT;
  v_current_user_home_id UUID;
  v_owner_home_id UUID;
BEGIN
  -- Get current user email from JWT
  v_current_user_email := current_setting('request.jwt.claims', true)::jsonb->>'email';

  -- No JWT = anonymous user, cannot share household
  IF v_current_user_email IS NULL THEN
    RETURN FALSE;
  END IF;

  -- Get current user's home_id
  SELECT home_id INTO v_current_user_home_id
  FROM users
  WHERE email = v_current_user_email;

  -- Current user has no home = cannot share household
  IF v_current_user_home_id IS NULL THEN
    RETURN FALSE;
  END IF;

  -- Get owner's home_id
  SELECT home_id INTO v_owner_home_id
  FROM users
  WHERE email = p_owner_email;

  -- Owner has no home = cannot share household
  IF v_owner_home_id IS NULL THEN
    RETURN FALSE;
  END IF;

  -- Check if they share the same home
  RETURN v_current_user_home_id = v_owner_home_id;
END;
$$;


