-- Name: toggle_recipe_like(uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.toggle_recipe_like(p_recipe_id uuid) RETURNS jsonb
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  v_user_email TEXT;
  v_recipe_owner TEXT;
  v_is_liked BOOLEAN;
BEGIN
  -- Get current user email from JWT
  v_user_email := current_setting('request.jwt.claims', true)::jsonb->>'email';

  IF v_user_email IS NULL THEN
    RAISE EXCEPTION 'not-authenticated';
  END IF;

  -- Check if recipe exists and get owner
  SELECT owner INTO v_recipe_owner FROM recipes WHERE id = p_recipe_id;

  -- SECURITY FIX: Generic error to prevent resource enumeration
  IF v_recipe_owner IS NULL THEN
    RAISE EXCEPTION 'operation-failed';
  END IF;

  -- Prevent liking own recipe
  IF v_recipe_owner = v_user_email THEN
    RAISE EXCEPTION 'operation-failed';
  END IF;

  -- Check if already liked
  SELECT EXISTS(
    SELECT 1 FROM recipe_likes
    WHERE recipe_id = p_recipe_id AND user_email = v_user_email
  ) INTO v_is_liked;

  IF v_is_liked THEN
    -- Unlike
    DELETE FROM recipe_likes
    WHERE recipe_id = p_recipe_id AND user_email = v_user_email;
    RETURN jsonb_build_object('liked', false);
  ELSE
    -- Like
    INSERT INTO recipe_likes (recipe_id, user_email)
    VALUES (p_recipe_id, v_user_email);
    RETURN jsonb_build_object('liked', true);
  END IF;
END;
$$;


--
-- Name: toggle_shopping_list_item(uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.toggle_shopping_list_item(p_item_id uuid) RETURNS jsonb
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  v_user_email TEXT;
  v_home_id UUID;
  v_is_checked BOOLEAN;
  v_list_id UUID;
  v_food_id UUID;
  v_quantity DECIMAL;
  v_unit TEXT;
BEGIN
  -- Get current user email from JWT
  v_user_email := current_setting('request.jwt.claims', true)::jsonb->>'email';

  IF v_user_email IS NULL THEN
    RAISE EXCEPTION 'not-authenticated';
  END IF;

  -- Get user's home_id
  SELECT home_id INTO v_home_id
  FROM users
  WHERE email = v_user_email;

  IF v_home_id IS NULL THEN
    RAISE EXCEPTION 'user-has-no-home';
  END IF;

  -- Get current state and verify home membership
  SELECT is_checked, shopping_list_id, food_id, quantity, display_unit
  INTO v_is_checked, v_list_id, v_food_id, v_quantity, v_unit
  FROM shopping_list_items
  WHERE id = p_item_id AND home_id = v_home_id;

  IF v_is_checked IS NULL THEN
    RAISE EXCEPTION 'item-not-found';
  END IF;

  -- Toggle the checked state
  UPDATE shopping_list_items
  SET
    is_checked = NOT v_is_checked,
    checked_at = CASE WHEN v_is_checked THEN NULL ELSE now() END
  WHERE id = p_item_id;

  -- Update shopping list modified timestamp
  UPDATE shopping_lists SET date_modified = now() WHERE id = v_list_id;

  -- If we're checking the item (not unchecking) and it has a food_id, add to pantry
  IF NOT v_is_checked AND v_food_id IS NOT NULL THEN
    INSERT INTO user_pantry (user_email, home_id, food_id, quantity, unit)
    VALUES (v_user_email, v_home_id, v_food_id, v_quantity, v_unit)
    ON CONFLICT (home_id, food_id) DO UPDATE SET
      quantity = COALESCE(user_pantry.quantity, 0) + COALESCE(EXCLUDED.quantity, 0),
      unit = COALESCE(EXCLUDED.unit, user_pantry.unit),
      added_at = NOW(),
      user_email = EXCLUDED.user_email;
  END IF;

  RETURN jsonb_build_object('is_checked', NOT v_is_checked);
END;
$$;


--
-- Name: trigger_food_search_text(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.trigger_food_search_text() RETURNS trigger
    LANGUAGE plpgsql
    SET search_path TO 'public'
    AS $$
BEGIN
  -- Rebuild if name changed, or if status changed involving 'approved'
  IF (TG_OP = 'UPDATE' AND
      (OLD.name IS DISTINCT FROM NEW.name OR OLD.status IS DISTINCT FROM NEW.status) AND
      (NEW.status = 'approved' OR OLD.status = 'approved')) THEN
    -- Rebuild search_text for all recipes using this food
    UPDATE recipes r SET
      search_text = concat_ws(' ',
        r.name,
        r.description,
        (SELECT string_agg(COALESCE(f.name, i.name), ' ')
         FROM ingredients i
         LEFT JOIN foods f ON i.food_id = f.id AND f.status = 'approved'
         WHERE i.recipe_id = r.id),
        (SELECT string_agg(i.form, ' ')
         FROM ingredients i
         WHERE i.recipe_id = r.id AND i.form IS NOT NULL)
      )
    WHERE r.id IN (
      SELECT DISTINCT recipe_id FROM ingredients WHERE food_id = NEW.id
    );
  END IF;

  RETURN NULL;
END;
$$;


