INSERT INTO public.email_templates (id, name, subject, html_body, text_body, variables, description, language, owner) VALUES ('39a1002d-6df6-4562-9b62-e46baf12176f', 'home_invitation', 'Du har blivit inbjuden till {{home_name}}', '<html><body><h1>Du har blivit inbjuden!</h1><p>Hej,</p><p>{{inviter_email}} har bjudit in dig till hemmet "{{home_name}}" p Matrummet.</p><p>Klicka p lnken nedan fr att acceptera inbjudan:</p><p><a href="{{accept_link}}">Acceptera inbjudan</a></p><p>Inbjudan r giltig i 7 dagar.</p><p>Om du inte har ett konto kan du skapa ett nr du klickar p lnken.</p></body></html>', E'Hej,\n\n{{inviter_email}} har bjudit in dig till hemmet "{{home_name}}" p Matrummet.\n\nKlicka p lnken nedan fr att acceptera inbjudan:\n{{accept_link}}\n\nInbjudan r giltig i 7 dagar.\n\nOm du inte har ett konto kan du skapa ett nr du klickar p lnken.', '{"home_name": "string", "accept_link": "string", "inviter_email": "string"}'::jsonb, 'Email sent when user is invited to join a home', 'sv', 'system@matrummet.local');
INSERT INTO public.email_templates (id, name, subject, html_body, text_body, variables, description, language, owner) VALUES ('067a3124-1d21-4ecf-8464-61738b953900', 'password_reset', 'terstll ditt lsenord', '<html><body><h1>terstll ditt lsenord</h1><p>Hej {{user_name}},</p><p>Klicka p lnken nedan fr att terstlla ditt lsenord:</p><p><a href="{{reset_link}}">terstll lsenord</a></p><p>Lnken r giltig i 24 timmar.</p></body></html>', E'Hej {{user_name}},\n\nKlicka p lnken nedan fr att terstlla ditt lsenord:\n{{reset_link}}\n\nLnken r giltig i 24 timmar.', '{"user_name": "string", "reset_link": "string"}'::jsonb, 'Email sent when user requests password reset', 'sv', 'system@matrummet.local');
INSERT INTO public.email_templates (id, name, subject, html_body, text_body, variables, description, language, owner) VALUES ('0697a63a-e1be-413c-a802-0521e2b6c656', 'welcome', 'Vlkommen till Matrummet!', '<html><body><h1>Vlkommen till Matrummet!</h1><p>Hej {{user_name}},</p><p>Tack fr att du skapade ett konto. Nu kan du brja spara och organisera dina favoritrecept!</p></body></html>', E'Hej {{user_name}},\n\nTack fr att du skapade ett konto. Nu kan du brja spara och organisera dina favoritrecept!', '{"user_name": "string"}'::jsonb, 'Welcome email sent to new users', 'sv', 'system@matrummet.local');
